    private static void dumpStackTraces(String tracesPath, ArrayList<Integer> firstPids, ProcessCpuTracker processCpuTracker, SparseArray<Boolean> lastPids, String[] nativeProcs) {
        int i;
        FileObserver observer = new AnonymousClass6(tracesPath, 8);
        observer.startWatching();
        if (firstPids != null) {
            try {
                int num = firstPids.size();
                for (i = POWER_CHECK_DELAY; i < num; i += SHOW_ERROR_UI_MSG) {
                    synchronized (observer) {
                        long sime = SystemClock.elapsedRealtime();
                        Process.sendSignal(((Integer) firstPids.get(i)).intValue(), SHOW_FACTORY_ERROR_UI_MSG);
                        observer.wait(1000);
                    }
                }
            } catch (InterruptedException e) {
                Slog.wtf(TAG, e);
            }
        }
        if (nativeProcs != null) {
            int[] pids = Process.getPidsForCommands(nativeProcs);
            if (pids != null) {
                int length = pids.length;
                for (int i2 = POWER_CHECK_DELAY; i2 < length; i2 += SHOW_ERROR_UI_MSG) {
                    int pid = pids[i2];
                    sime = SystemClock.elapsedRealtime();
                    Debug.dumpNativeBacktraceToFile(pid, tracesPath);
                }
            }
        }
        if (processCpuTracker != null) {
            processCpuTracker.init();
            System.gc();
            processCpuTracker.update();
            try {
                synchronized (processCpuTracker) {
                    processCpuTracker.wait(500);
                }
            } catch (InterruptedException e2) {
            }
            try {
                processCpuTracker.update();
                int N = processCpuTracker.countWorkingStats();
                int numProcs = POWER_CHECK_DELAY;
                for (i = POWER_CHECK_DELAY; i < N && numProcs < GC_BACKGROUND_PROCESSES_MSG; i += SHOW_ERROR_UI_MSG) {
                    Stats stats = processCpuTracker.getWorkingStats(i);
                    if (lastPids.indexOfKey(stats.pid) >= 0) {
                        numProcs += SHOW_ERROR_UI_MSG;
                        try {
                            synchronized (observer) {
                                long stime = SystemClock.elapsedRealtime();
                                Process.sendSignal(stats.pid, SHOW_FACTORY_ERROR_UI_MSG);
                                observer.wait(1000);
                            }
                        } catch (InterruptedException e3) {
                            Slog.wtf(TAG, e3);
                        }
                    }
                }
            } catch (Throwable th) {
                observer.stopWatching();
            }
        }
        IHwBinderMonitor iBinderM = HwServiceFactory.getIHwBinderMonitor();
        if (iBinderM != null) {
            iBinderM.writeTransactonToTrace(tracesPath);
        }
        observer.stopWatching();
    }

