    public void swapDockedAndFullscreenStack() throws RemoteException {
        enforceCallingPermission("android.permission.MANAGE_ACTIVITY_STACKS", "swapDockedAndFullscreenStack()");
        synchronized (this) {
            long ident;
            try {
                TaskRecord topTask;
                ArrayList allTasks;
                boostPriorityForLockedSection();
                ident = Binder.clearCallingIdentity();
                ActivityStack fullscreenStack = this.mStackSupervisor.getStack(SHOW_ERROR_UI_MSG);
                if (fullscreenStack != null) {
                    topTask = fullscreenStack.topTask();
                } else {
                    topTask = null;
                }
                ActivityStack dockedStack = this.mStackSupervisor.getStack(SHOW_FACTORY_ERROR_UI_MSG);
                if (dockedStack != null) {
                    allTasks = dockedStack.getAllTasks();
                } else {
                    allTasks = null;
                }
                if (!(topTask == null || allTasks == null)) {
                    if (allTasks.size() != 0) {
                        this.mWindowManager.prepareAppTransition(18, MONITOR_THREAD_CPU_USAGE);
                        this.mStackSupervisor.moveTaskToStackLocked(topTask.taskId, SHOW_FACTORY_ERROR_UI_MSG, MONITOR_THREAD_CPU_USAGE, MONITOR_THREAD_CPU_USAGE, "swapDockedAndFullscreenStack", VALIDATE_UID_STATES, VALIDATE_UID_STATES);
                        int size = allTasks.size();
                        for (int i = POWER_CHECK_DELAY; i < size; i += SHOW_ERROR_UI_MSG) {
                            int id = ((TaskRecord) allTasks.get(i)).taskId;
                            if (id != topTask.taskId) {
                                this.mStackSupervisor.moveTaskToStackLocked(id, SHOW_ERROR_UI_MSG, VALIDATE_UID_STATES, MONITOR_THREAD_CPU_USAGE, "swapDockedAndFullscreenStack", VALIDATE_UID_STATES, VALIDATE_UID_STATES);
                            }
                        }
                        this.mStackSupervisor.ensureActivitiesVisibleLocked(null, POWER_CHECK_DELAY, MONITOR_THREAD_CPU_USAGE);
                        this.mStackSupervisor.resumeFocusedStackTopActivityLocked();
                        this.mWindowManager.executeAppTransition();
                        Binder.restoreCallingIdentity(ident);
                        resetPriorityAfterLockedSection();
                        return;
                    }
                }
                Slog.w(TAG, "Unable to swap tasks, either docked or fullscreen stack is empty.");
                Binder.restoreCallingIdentity(ident);
                resetPriorityAfterLockedSection();
            } catch (Throwable th) {
                resetPriorityAfterLockedSection();
            }
        }
    }

